  let v = _v;
  if (typeof v === 'string') v = parseFloat(v.indexOf('%') > -1 ? v.substring(0, v.length - 1) : v, 10);
  if (Number.isNaN(v)) v = 0;
  else if (v > 100) v = 100;
  else if (v < 0) v = 0;
  return v;
