  if (r === cmax) h = bc - gc;
  else if (g === cmax) h = 2 + rc - bc;
  else h = 4.0 + gc - rc;
  h = (h / 6.0) % 1.0;
  if (h < 0) h += 1.0;

  h = Math.round(h * 360);
  s = Math.round(s * 100);
