export const Waveform = ({sample}) => {

const { data } = getWaveformFile(waveformFileName)
const peaks = data.filter(point => point >= 0)
const ratio = Math.max(...peaks) / 100
const normalized = peaks.map(point => Math.round(point / ratio))

}
