// Organized
import React, { useContext, useEffect, useState, useRef } from "react"
import Rating from '@material-ui/lab/Rating';
import { SampleContext } from "../sample/SampleProvider"
import AudioPlayer from 'react-h5-audio-player'
import { Link } from "react-router-dom"
import "./Samples.css"
import WaveSurfer from "wavesurfer.js";
import 'react-h5-audio-player/lib/styles.css'
import { HexColorPicker } from "react-colorful";
import "react-colorful/dist/index.css";

export const SampleDetails = (props) => {

    const [rSampleItem, setRSampleValue] = useState(0)
    const [value, setValue] = useState(null)
    const [volume, setVolume] = useState(null)
    const [currentSample, setCurrentSample] = useState({})
    const [noneLeft, setNoneLeft] = useState(false)
    const [zeroed, setZeroed] = useState(false)
    const [itemsLeftToShow, setitemsLeftToShow] = useState([])
    const [color, setColor] = useState("#aabbcc");

    const waveformRef = useRef(null);

    const { favorites,
        getUsers,
        getFavorites,
        getRandomSample,
        randomSample,
        skipped,
        addRatings,
        getRatings,
        singleSample,
        ratings,
        getSkipped,
        randomSamplesLoaded,
        singleSampleLoaded,
        getSampleById,
    } = useContext(SampleContext)

    useEffect(() => {
        waveformRef.current = WaveSurfer.create({ 
          container: waveformRef.current,
          cursorColor: "transparent",
          backgroundColor: "black"
        });
        waveformRef.current.load('http://ia902606.us.archive.org/35/items/shortpoetry_047_librivox/song_cjrg_teasdale_64kb.mp3')
        waveformRef.current.setWaveColor(color)
      }, []);

      useEffect(() => {
        waveformRef.current.setWaveColor(color)
      }, [color]);

    function comparer(otherArray) {
        return function (current) {
            return otherArray.filter(function (other) {
                return other.sample_id == current.id
            }).length == 0
        }
    }

    const addComment = () => {
        addRatings({
            sample: currentSample.id,
            color: color,
            rating: value,
            loudness: volume
        })
    }

    useEffect(() => {
        getUsers().then(getSkipped).then(getFavorites).then(getSampleById(2))
    }, [])

    useEffect(() => {
        console.log(singleSample)
    }, [singleSample])

    if (singleSampleLoaded === true){
        return (
            <>
            <div class="sampleContainer">
            <img class="img" src={currentSample.sample_image}></img>
                <div class="link_card button4"><Link to={`/browse/${currentSample.id}`}>{singleSample.name}</Link></div>
                <div ref={waveformRef} />
                <AudioPlayer
                    autoPlayAfterSrcChange={false}
                    src={currentSample.audio_url}
                    onPlay={e => console.log("onPlay")}
                    onVolumeChange={e => setVolume(e.target.volume)} />
                <div className="value" style={{ color: color }}>
                Current color is {color}
                </div>
                <Rating 
                value={value}
                onChange={(event, newValue) => {setValue(newValue)}}/>
            </div>
            </>
        )
    }
    else{return(<div ref={waveformRef} />)}
}
