// Organized
import React, { useContext, useEffect, useState } from "react"
import { SampleContext } from "../sample/SampleProvider"
import { Browse } from "./Browse"
import AudioPlayer from 'react-h5-audio-player'
import { Link } from "react-router-dom"
import "./Browse.css"
import 'react-h5-audio-player/lib/styles.css'

export const BrowseSamples = ({sample}) => {

    const addSampleToFavorites = () => {
        addFavorites({
          user: parseInt(localStorage.getItem("user_number")),
          sample: sample.id
      })
      }

    // Context
    const {favorites,  
        getUsers,
        getFavorites,
        addFavorites,
        getSamples,
        samples, 
        randomSample,
        getRandomSample,
    } = useContext(SampleContext)

    // State 

    const [ filteredSamples, setFiltered ] = useState([])

    // Hooks

    useEffect(() => {
        getSamples()
        getUsers()
        getFavorites()
        getRandomSample()  
    setFiltered([randomSample])
    }, [])

    useEffect(() => {
        getRandomSample()  
    setFiltered([randomSample])
    }, [favorites])

    // JSX
        return (
            <>
            <div className="samples">
                <div class="sampleCard">
                        </div> 
                {
                    filteredSamples.map(sample => {
                        return <>
<section class="sampleCard">
  <div class="link_card button4"><Link to={`/browse/${sample.id}`}>{sample.name}</Link></div>
        <AudioPlayer 
            preload
            src={sample.audio_url}
            onPlay={e => console.log("onPlay")}/>
        <button class="button5" onClick={addSampleToFavorites}>Love</button>
        <button class="button3" onClick={Browse}>Skip</button>
</section>
</>
                    })
                }
            </div> 
            </>     
        )
    }
