// Organized
import React, { useContext, useEffect, useState, useRef } from "react"
import WaveSurfer from "wavesurfer.js";
import { SampleContext } from "./SampleProvider"
import { Sample } from "./Sample"
import AudioPlayer from 'react-h5-audio-player'
import "./Samples.css"

export const SampleList = ({sample}) => {

    const waveformRef = useRef(samples.map(() => createRef()))
    const downloadFile = () => {
        window.location.href = sample.audio_url
      }

    // Context

    useEffect(() => {
        waveformRef.current = WaveSurfer.create({ 
            container: waveformRef.current,
            waveColor: 'purple'
            })
            waveformRef.current.load('http://ia902606.us.archive.org/35/items/shortpoetry_047_librivox/song_cjrg_teasdale_64kb.mp3')
    }, [])

    const {favorites, 
        filterValue, 
        getSamples,
        samples, 
        searchTerms,
        getUsers,
        user,
        getUserById,
    } = useContext(SampleContext)

    // Hooks
    let currentUser = parseInt(localStorage.getItem("user_number"))

    useEffect(() => {
        getSamples()
        getUsers()
        getUserById(currentUser)
    }, [])

    // JSX

        return (
            <>
            <div className="samples">
            <div ref={waveformRef}></div>
                <div class="sampleCard">
                        </div>
                {
                    samples.map(sample => {        
                        return (
                        <>
                        <div class="sampleContainer">
                        <img class="img" src={sample.sample_image}></img>
                        <div ref={waveformRef}></div>
                        <AudioPlayer 
                            preload="true"
                            src={sample.audio_url}
                            onPlay={e => console.log("onPlay")}/>
                            <button class="button3" onClick={downloadFile}>Download Sample</button>
                        </div>
                        </>
                            )
                    })
                }
            </div> 
            </>
        )
}
