// Organized
import React, { useContext, useEffect, useState } from "react"
import { SampleContext } from "./SampleProvider"
import { Sample } from "./Sample"
import "./Samples.css"
import 'react-h5-audio-player/lib/styles.css'

export const BrowseSamples = (props) => {

    const {favorites, 
        filterValue, 
        getCustomers,
        getFavorites,
        getSamples,
        getUserFriends, 
        samples, 
        searchTerms,
        setFilter,
           } = useContext(SampleContext)
    const [ filteredSamples, setFiltered ] = useState([])

    useEffect(() => {
        getSamples()
        getCustomers()
        getUserFriends()
        getFavorites()
        setFilter("0")
    }, [])
    useEffect(() => {
        let samplesToDisplay = samples
        let currentlyFiltered = samples
                const notUser = currentlyFiltered.filter(byUser => byUser.customerId != parseInt(localStorage.customer))
                samplesToDisplay = notUser
                let randomInt = Math.floor(Math.random() * samplesToDisplay.length + 1);
                let randomSample = samplesToDisplay.find(thisSample => thisSample.id === randomInt)

    setFiltered(samplesToDisplay)
    }, [searchTerms, samples, filterValue])

        return (
            <>
            <div className="samples">
                <div class="sampleCard">
                        </div> 
                {
                    randomSample.map(sample => {
                        return <Sample key={sample.id} sample={sample} />
                    })
                }
            </div> 
            </>
        )
    }
